mapper.select:
SELECT COUNT(*)
FROM REPORT
WHERE USER_NO = #{userNo}
  AND REPORT_COM = #{reportCom}

mapper.insert:
INSERT INTO REPORT(
				   USER_NO
				 , REPORT_COM
				 , REASON
				 )
				 VALUES
				 (
				   #{userNo}
				 , #{reportCom}
				 , #{reason}
				 )

mapper.select:
SELECT COUNT(*)
FROM REPORT
WHERE USER_NO = #{userNo}
  AND REPORT_COM = #{reportCom}
  AND REPORT_REP = #{reportRep}

mapper.insert:
INSERT INTO REPORT(
				   USER_NO
				 , REPORT_COM
				 , REPORT_REP
				 , REASON
				 )
				 VALUES
				 (
				   #{userNo}
				 , #{reportCom}
				 , #{reportRep}
				 , #{reason}
				 )
